 3→fun nonLambdaCalculation(inputList: List<Int>): Int {
 4→    var result = 0
 5→    for (input in inputList) {
 6→        if (input == -1) break
 7→        if (input % 2 == 0) {
 8→            result += input * input
 9→        }
10→    }
11→    return result
12→}
