 7→class ReceiptText(val template: String) : (Int) -> String {
 8→    override fun invoke(amount: Int) = template.replace("%", amount.toString())
 9→
10→    companion object {
11→        @JvmStatic
12→        fun main(args: Array<String>) {
13→            val receiptText = ReceiptText("Bill amount: $%")
14→            println(receiptText(5))
15→            // or
16→            println(ReceiptText("Bill amount: $%")(5))
