21→object ReceiptTextFunc {
22→    fun receiptText(template: String): (Int) -> String = { amount ->
23→        template.replace("%", amount.toString())
24→    }
25→
26→    @JvmStatic
27→    fun main(args: Array<String>) {
28→        val hof = receiptText("Bill amount: $%")
29→        println(hof(5))
30→        //or
31→        println(receiptText("Bill amount: $%")(5))
32→    }
33→}
