35→// An object with invoke operators
36→object ReceiptTextObj {
37→    operator fun invoke(amount: Int): String =
38→        receiptText("Bill amount: $%")(amount)
39→
40→    operator fun invoke(template: String, amount: Int): String =
41→        receiptText(template)(amount)
42→
43→    @JvmStatic
44→    fun main(args: Array<String>) {
45→        println(ReceiptTextObj(5))
46→        println(ReceiptTextObj("Bill amount: $%", 5))
