50→object ReceiptTextCombo {
51→    @JvmStatic
52→    fun main(args: Array<String>) {
53→        val functions =
54→            mutableListOf<(Int) -> String>()
55→                .apply {
56→                    add(ReceiptText("Thank you for $%!"))
57→                    add(receiptText("TA %"))
58→                    add(ReceiptTextObj::invoke)
59→                }
60→        val receipts = functions.mapIndexed { i, func -> func(i + 100) }
61→        println(receipts)
62→    }
63→}

